import React, { useEffect, useState, useCallback } from 'react'
import { commentApi } from '@/services/api/comment.service'
import type { CommentWithUser } from '@/types/comment.types'
import { useSelector } from 'react-redux'
import type { RootState } from '@/store/stores'
import { MessageCircle, Edit2, Trash2, Send } from 'lucide-react'

interface Props {
  lessonId?: string
  problemId?: string
}

const CommentsSection: React.FC<Props> = ({ lessonId, problemId }) => {
  const contextId = lessonId || problemId
  const contextType = lessonId ? 'lesson' : 'problem'

  const [comments, setComments] = useState<CommentWithUser[]>([])
  const [loading, setLoading] = useState(false)
  const [content, setContent] = useState('')
  const [editingId, setEditingId] = useState<string | null>(null)
  const [editingContent, setEditingContent] = useState('')
  const [submitting, setSubmitting] = useState(false)
  const auth = useSelector((state: RootState) => state.auth.session)

  const fetchComments = useCallback(async () => {
    if (!contextId) return
    setLoading(true)
    try {
      const data =
        contextType === 'lesson'
          ? (await commentApi.listByLesson(contextId)).data
          : (await commentApi.listByProblem(contextId)).data
      setComments(data)
    } catch (err) {
      console.error('Failed to load comments', err)
    } finally {
      setLoading(false)
    }
  }, [contextId, contextType])

  useEffect(() => {
    if (!contextId) return
    fetchComments()
  }, [contextId, contextType])

  // Debug: log current user and comments
  useEffect(() => {
    console.log('Current auth user:', auth.user)
    console.log('Comments:', comments)
  }, [auth.user, comments])

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!content.trim() || !contextId) return

    setSubmitting(true)
    try {
      await commentApi.create({
        content,
        ...(contextType === 'lesson'
          ? { lessonId: contextId }
          : { problemId: contextId }),
      })
      // Fetch to get user info
      await fetchComments()
      setContent('')
    } catch (err) {
      console.error('Failed to post comment', err)
    } finally {
      setSubmitting(false)
    }
  }

  const handleEdit = (id: string, currentContent: string) => {
    setEditingId(id)
    setEditingContent(currentContent)
  }

  const handleSaveEdit = async (id: string) => {
    if (!editingContent.trim()) return

    setSubmitting(true)
    try {
      await commentApi.update(id, editingContent.trim())
      setEditingId(null)
      setEditingContent('')
      await fetchComments()
    } catch (err) {
      console.error('Failed to update comment', err)
    } finally {
      setSubmitting(false)
    }
  }

  const handleDelete = async (id: string) => {
    if (!confirm('Are you sure you want to delete this comment?')) return
    try {
      await commentApi.delete(id)
      setComments(prev => prev.filter(c => c.comment.id !== id))
    } catch (err) {
      console.error('Delete failed', err)
    }
  }

  const getUserName = (user: CommentWithUser['user']) => {
    if (!user) return 'Anonymous'
    if (user.firstName && user.lastName)
      return `${user.firstName} ${user.lastName}`
    if (user.firstName) return user.firstName
    if (user.lastName) return user.lastName
    return user.email
  }

  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr)
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    })
  }

  return (
    <section className="mt-10 rounded-lg border border-gray-800 bg-[#1a1b23] p-6">
      {/* Header */}
      <div className="mb-6 flex items-center gap-3">
        <MessageCircle className="h-5 w-5 text-blue-400" />
        <h3 className="text-xl font-semibold text-white">
          Comments ({comments.length})
        </h3>
      </div>

      {/* Comment Form */}
      {auth.isAuthenticated ? (
        <form onSubmit={handleSubmit} className="mb-6">
          <textarea
            className="w-full rounded-lg border border-gray-700 bg-[#252730] px-4 py-3 text-white placeholder-gray-500 transition-colors focus:border-blue-500 focus:outline-none focus:ring-1 focus:ring-blue-500"
            rows={3}
            value={content}
            onChange={e => setContent(e.target.value)}
            placeholder="Share your thoughts..."
            disabled={submitting}
          />
          <div className="mt-3 flex justify-end">
            <button
              className="inline-flex items-center gap-2 rounded-lg bg-blue-600 px-4 py-2 font-medium text-white transition-colors hover:bg-blue-700 disabled:cursor-not-allowed disabled:opacity-50"
              type="submit"
              disabled={submitting || !content.trim()}
            >
              <Send className="h-4 w-4" />
              Post Comment
            </button>
          </div>
        </form>
      ) : (
        <div className="mb-6 rounded-lg border border-gray-700 bg-[#252730] p-4 text-center">
          <p className="text-gray-400">
            <a href="/login" className="text-blue-400 hover:underline">
              Log in
            </a>{' '}
            to post comments.
          </p>
        </div>
      )}

      {/* Comments List */}
      {loading ? (
        <div className="py-8 text-center">
          <div className="inline-block h-6 w-6 animate-spin rounded-full border-2 border-blue-600 border-t-transparent"></div>
        </div>
      ) : comments.length === 0 ? (
        <div className="rounded-lg border border-gray-700 bg-[#252730] p-8 text-center">
          <MessageCircle className="mx-auto h-8 w-8 text-gray-600 opacity-50" />
          <p className="mt-2 text-gray-500">
            No comments yet. Be the first to comment!
          </p>
        </div>
      ) : (
        <ul className="space-y-4">
          {comments.map(({ comment, user }) => (
            <li
              key={comment.id}
              className="rounded-lg border border-gray-700 bg-[#252730] p-4 transition-colors hover:border-gray-600"
            >
              {/* User Info */}
              <div className="mb-3 flex items-center justify-between">
                <div className="flex items-center gap-3">
                  {user?.avatar ? (
                    <img
                      src={user.avatar}
                      alt={getUserName(user)}
                      className="h-8 w-8 rounded-full object-cover"
                    />
                  ) : (
                    <div className="flex h-8 w-8 items-center justify-center rounded-full bg-blue-600">
                      <span className="text-xs font-semibold text-white">
                        {getUserName(user).charAt(0).toUpperCase()}
                      </span>
                    </div>
                  )}
                  <div>
                    <p className="font-medium text-white">
                      {getUserName(user)}
                    </p>
                    <p className="text-xs text-gray-400">
                      {formatDate(comment.createdAt)}
                    </p>
                  </div>
                </div>

                {/* Action Buttons */}
                {auth.isAuthenticated &&
                  (auth.user?.id === comment.userId ||
                    (auth.user?.role &&
                      ['owner', 'teacher'].includes(auth.user.role))) && (
                    <div className="flex gap-2">
                      {auth.user?.id === comment.userId && (
                        <button
                          onClick={() =>
                            handleEdit(comment.id, comment.content)
                          }
                          className="rounded p-1.5 text-gray-400 transition-colors hover:bg-gray-700 hover:text-yellow-400"
                          title="Edit"
                          disabled={editingId !== null}
                        >
                          <Edit2 className="h-4 w-4" />
                        </button>
                      )}
                      <button
                        onClick={() => handleDelete(comment.id)}
                        className="rounded p-1.5 text-gray-400 transition-colors hover:bg-gray-700 hover:text-red-400"
                        title="Delete"
                        disabled={editingId !== null}
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                    </div>
                  )}
              </div>

              {/* Comment Content */}
              {editingId === comment.id ? (
                <div className="space-y-3">
                  <textarea
                    className="w-full rounded-lg border border-blue-500 bg-[#1a1b23] px-3 py-2 text-white placeholder-gray-500 focus:outline-none focus:ring-1 focus:ring-blue-500"
                    rows={2}
                    value={editingContent}
                    onChange={e => setEditingContent(e.target.value)}
                  />
                  <div className="flex justify-end gap-2">
                    <button
                      onClick={() => setEditingId(null)}
                      className="rounded px-3 py-1 text-sm text-gray-400 transition-colors hover:bg-gray-700"
                      disabled={submitting}
                    >
                      Cancel
                    </button>
                    <button
                      onClick={() => handleSaveEdit(comment.id)}
                      className="rounded bg-blue-600 px-3 py-1 text-sm font-medium text-white transition-colors hover:bg-blue-700 disabled:opacity-50"
                      disabled={submitting || !editingContent.trim()}
                    >
                      Save
                    </button>
                  </div>
                </div>
              ) : (
                <p className="whitespace-pre-wrap text-gray-200">
                  {comment.content}
                </p>
              )}
            </li>
          ))}
        </ul>
      )}
    </section>
  )
}

export default CommentsSection

import React, { useState, useEffect, useMemo } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { ArrowLeft, Search } from 'lucide-react'
import Input from '@/components/common/Input/Input'
import Button from '@/components/common/Button/Button'
import { ExamSubmission, Exam } from '@/types/exam.types'
import LoadingSpinner from '@/components/common/LoadingSpinner'
import { useAuth } from '@/hooks/api/useAuth'

const ExamResults: React.FC = () => {
  const { examId } = useParams<{ examId: string }>()
  const navigate = useNavigate()
  const [exam, setExam] = useState<Exam | null>(null)
  const [submissions, setSubmissions] = useState<ExamSubmission[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState('')
  const { user } = useAuth()

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true)
        // Mock exam data
        const mockExam: Exam = {
          id: examId || '1',
          title: 'Algorithms Midterm Test',
          password: 'test123',
          duration: 90,
          challenges: [],
          startDate: new Date().toISOString(),
          endDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString(),
          createdBy: 'lecturer1',
          createdAt: new Date().toISOString(),
        }

        // Mock submissions data
        const mockSubmissions: ExamSubmission[] = [
          {
            id: '1',
            userId: 'student1',
            examId: examId || '1',
            user: {
              id: 'student1',
              firstname: 'John',
              lastname: 'Doe',
              email: 'john@example.com',
              role: 'student',
              avatar: '',
              createdAt: new Date().toISOString(),
              lastLoginAt: new Date().toISOString(),
            },
            solutions: [],
            totalScore: 85,
            startedAt: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
            submittedAt: new Date(
              Date.now() - 1 * 60 * 60 * 1000
            ).toISOString(),
            duration: 60,
          },
          {
            id: '2',
            userId: 'student2',
            examId: examId || '1',
            user: {
              id: 'student2',
              firstname: 'Jane',
              lastname: 'Smith',
              email: 'jane@example.com',
              role: 'student',
              avatar: '',
              createdAt: new Date().toISOString(),
              lastLoginAt: new Date().toISOString(),
            },
            solutions: [],
            totalScore: 92,
            startedAt: new Date(Date.now() - 3 * 60 * 60 * 1000).toISOString(),
            submittedAt: new Date(
              Date.now() - 2 * 60 * 60 * 1000
            ).toISOString(),
            duration: 75,
          },
          {
            id: '3',
            userId: 'student3',
            examId: examId || '1',
            user: {
              id: 'student3',
              firstname: 'Bob',
              lastname: 'Johnson',
              email: 'bob@example.com',
              role: 'student',
              avatar: '',
              createdAt: new Date().toISOString(),
              lastLoginAt: new Date().toISOString(),
            },
            solutions: [],
            totalScore: 78,
            startedAt: new Date(Date.now() - 4 * 60 * 60 * 1000).toISOString(),
            submittedAt: new Date(
              Date.now() - 3 * 60 * 60 * 1000
            ).toISOString(),
            duration: 85,
          },
        ]

        setExam(mockExam)
        setSubmissions(mockSubmissions)
      } catch (error) {
        console.error('Failed to fetch data:', error)
      } finally {
        setLoading(false)
      }
    }

    fetchData()
  }, [examId])

  // Helper to get full name
  const getFullName = (firstname?: string, lastname?: string) => {
    return `${firstname || ''} ${lastname || ''}`.trim()
  }

  const filteredSubmissions = useMemo(() => {
    return submissions
      .filter(submission => {
        const fullName = getFullName(
          submission.user?.firstname,
          submission.user?.lastname
        )
        return fullName.toLowerCase().includes(searchTerm.toLowerCase())
      })
      .sort(
        (a, b) =>
          new Date(b.submittedAt).getTime() - new Date(a.submittedAt).getTime()
      )
  }, [submissions, searchTerm])

  if (loading) {
    return <LoadingSpinner />
  }

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    })
  }

  const isInstructor =
    !!user &&
    (user.role === 'admin' ||
      user.role === 'lecturer' ||
      (exam ? user.id === exam.createdBy : false))

  return (
    <div className="min-h-screen" style={{backgroundColor: 'var(--background-color)', color: 'var(--text-color)'}}>
      <div className="mx-auto max-w-4xl space-y-8 px-4 py-10">
        <header className="card p-6">
          <div className="flex flex-col gap-4 sm:flex-row sm:items-center sm:justify-between">
              <div>
              <Button onClick={() => navigate(-1)} variant="secondary" size="sm" icon={<ArrowLeft size={14} />}>Back</Button>
              <h1 className="mt-3 text-2xl font-semibold" style={{color: 'var(--text-color)'}}>{exam?.title}</h1>
              <p className="mt-1 text-sm muted">Recently submitted solutions from your cohort</p>
            </div>

            {isInstructor && (
              <Button onClick={() => navigate(`/exam/${examId}/results/manage`)} variant="secondary" size="sm">Open instructor view</Button>
            )}
          </div>
        </header>

        <section className="rounded-lg border p-4" style={{borderColor: 'var(--surface-border)', backgroundColor: 'var(--exam-panel-bg)'}}>
          <div className="flex flex-col gap-3 sm:flex-row sm:items-center sm:justify-between">
            <div>
              <p className="text-xs uppercase tracking-wider muted">Submissions</p>
              <h2 className="text-lg font-semibold" style={{color: 'var(--text-color)'}}>Who has turned in?</h2>
            </div>
            <div className="w-full sm:w-64">
              <Input
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
                placeholder="Search student..."
                icon={<Search size={16} />}
                className="w-full"
              />
            </div>
          </div>

          {filteredSubmissions.length === 0 ? (
            <div className="mt-4 rounded-md p-6 text-center muted" style={{border: '1px dashed var(--surface-border)'}}>No one has submitted yet. Your results will appear here once classmates finish.</div>
          ) : (
            <ul className="mt-4 space-y-3">
              {filteredSubmissions.map(submission => (
                <li key={submission.id} className="flex flex-wrap items-center justify-between gap-4 rounded-md border p-4" style={{borderColor: 'var(--surface-border)', backgroundColor: 'var(--editor-bg)'}}>
                  <div>
                    <p className="text-base font-semibold" style={{color: 'var(--text-color)'}}>{getFullName(submission.user?.firstname, submission.user?.lastname) || 'Unknown student'}</p>
                    <p className="text-xs uppercase tracking-wider muted">{formatDate(submission.submittedAt)}</p>
                  </div>
                  <div className="text-right">
                    <p className="text-[10px] uppercase tracking-wider muted">Total score</p>
                    <span className={`mt-1 inline-flex rounded-full px-3 py-1 text-sm font-semibold`} style={{backgroundColor: submission.totalScore >= 90 ? 'rgba(16,185,129,0.08)' : 'rgba(59,130,246,0.06)', color: 'var(--text-color)'}}>{submission.totalScore}%</span>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </section>
      </div>
    </div>
  )
}

export default ExamResults

const scoreClass = (score: number) => {
  if (score >= 90) return 'bg-emerald-500/15 text-emerald-200'
  if (score >= 80) return 'bg-primary-500/15 text-primary-200'
  if (score >= 70) return 'bg-amber-500/15 text-amber-200'
  if (score >= 60) return 'bg-rose-500/15 text-rose-200'
  return 'bg-rose-600/20 text-rose-100'
}

import React, { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { ArrowLeft, Code, Check, X } from 'lucide-react'
import { ExamSubmission } from '@/types/exam.types'
import LoadingSpinner from '@/components/common/LoadingSpinner'
import Button from '@/components/common/Button/Button'

const ExamSubmissionDetail: React.FC = () => {
  const { examId, submissionId } = useParams<{
    examId: string
    submissionId: string
  }>()
  const navigate = useNavigate()
  const [submission, setSubmission] = useState<ExamSubmission | null>(null)
  const [loading, setLoading] = useState(true)
  const [activeTab, setActiveTab] = useState<'overview' | 'details'>('overview')

  React.useEffect(() => {
    const fetchSubmission = async () => {
      try {
        setLoading(true)
        const mockSubmission: ExamSubmission = {
          id: submissionId || '1',
          userId: 'student1',
          examId: examId || '1',
          user: {
            id: 'student1',
            firstname: 'John',
            lastname: 'Doe',
            email: 'john@example.com',
            role: 'student',
            avatar: '',
            createdAt: new Date().toISOString(),
            lastLoginAt: new Date().toISOString(),
          },
          solutions: [
            {
              challengeId: '1',
              code: 'function twoSum(nums, target) {\n  // Solution code here\n}',
              language: 'javascript',
              score: 80,
              results: [
                {
                  testCaseId: '1',
                  passed: true,
                  actualOutput: '[0, 1]',
                  expectedOutput: '[0, 1]',
                },
                {
                  testCaseId: '2',
                  passed: true,
                  actualOutput: '[1, 2]',
                  expectedOutput: '[1, 2]',
                },
              ],
              submittedAt: new Date().toISOString(),
            },
          ],
          totalScore: 80,
          startedAt: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
          submittedAt: new Date(Date.now() - 1 * 60 * 60 * 1000).toISOString(),
          duration: 60,
        }
        setSubmission(mockSubmission)
      } catch (error) {
        console.error('Failed to fetch submission:', error)
      } finally {
        setLoading(false)
      }
    }

    fetchSubmission()
  }, [examId, submissionId])

  if (loading) {
    return <LoadingSpinner />
  }

  if (!submission) {
    return (
      <div className="flex min-h-screen items-center justify-center bg-[#020309] text-gray-200">
        <div className="rounded-3xl border border-white/5 bg-[#05060d] px-12 py-16 text-center">
          <h2 className="text-2xl font-semibold text-white">
            Submission not found
          </h2>
        </div>
      </div>
    )
  }

  const formatDate = (dateString: string) =>
    new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    })

  return (
    <div className="min-h-screen" style={{backgroundColor: 'var(--background-color)', color: 'var(--text-color)'}}>
      <div className="mx-auto max-w-6xl space-y-6 px-4 py-10">
        <div className="flex flex-wrap items-center gap-4 p-4 rounded-md" style={{border: '1px solid var(--surface-border)', backgroundColor: 'var(--exam-panel-bg)'}}>
          <Button onClick={() => navigate(-1)} variant="secondary" size="sm" icon={<ArrowLeft size={14} />}>Back</Button>
          <div>
            <h1 className="text-lg font-semibold" style={{color: 'var(--text-color)'}}>
              {submission.user?.firstname} {submission.user?.lastname}
            </h1>
            <p className="text-sm muted">Submitted {formatDate(submission.submittedAt)} â€¢ Duration {submission.duration} mins</p>
          </div>
        </div>

        <div className="rounded-md border p-4" style={{borderColor: 'var(--surface-border)', backgroundColor: 'var(--exam-panel-bg)'}}>
            <div className="flex gap-4 border-b pb-4" style={{borderColor: 'var(--surface-border)'}}>
            {(['overview', 'details'] as const).map(tab => (
              <Button
                key={tab}
                variant={activeTab === tab ? 'secondary' : 'ghost'}
                size="sm"
                className={`rounded-full px-4 py-2 text-xs font-semibold uppercase tracking-wider ${activeTab === tab ? '' : 'muted'}`}
                onClick={() => setActiveTab(tab)}
              >
                {tab}
              </Button>
            ))}
          </div>

          {activeTab === 'overview' && (
            <div className="space-y-6 pt-4">
              <div className="flex flex-wrap gap-6 p-4 rounded-md" style={{border: '1px solid var(--surface-border)', backgroundColor: 'var(--editor-bg)'}}>
                <OverviewStat label="Total score" value={`${submission.totalScore}%`} />
                <OverviewStat label="Solutions" value={submission.solutions.length} />
                <OverviewStat label="Time spent" value={`${submission.duration}m`} />
              </div>

              <div className="rounded-md p-4" style={{border: '1px solid var(--surface-border)', backgroundColor: 'var(--editor-bg)'}}>
                <h3 className="text-lg font-semibold" style={{color: 'var(--text-color)'}}>Challenge results</h3>
                <div className="mt-4 space-y-4">
                  {submission.solutions.map((solution, index) => (
                    <div key={solution.challengeId} className="rounded-md p-4" style={{border: '1px solid var(--surface-border)', backgroundColor: 'var(--exam-panel-bg)'}}>
                      <div className="flex items-center justify-between">
                        <span className="text-sm muted">Challenge {index + 1}</span>
                        <span className={`rounded-full px-3 py-1 text-xs font-semibold`} style={{backgroundColor: solution.score >= 80 ? 'rgba(59,130,246,0.06)' : 'rgba(240,68,68,0.06)'}}>{solution.score}%</span>
                      </div>
                      <div className="mt-3 flex flex-wrap gap-2 text-xs muted">
                        {solution.results?.map((result, idx) => (
                          <span key={idx} className={`inline-flex items-center gap-1 rounded-full px-3 py-1`} style={{backgroundColor: result.passed ? 'rgba(16,185,129,0.06)' : 'rgba(240,68,68,0.06)'}}>
                            {result.passed ? <Check size={12} /> : <X size={12} />} Test {idx + 1}
                          </span>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {activeTab === 'details' && (
            <div className="space-y-6 pt-4">
              {submission.solutions.length === 0 ? (
                <div className="rounded-md p-6 text-center muted" style={{border: '1px dashed var(--surface-border)'}}>No solution details available</div>
              ) : (
                submission.solutions.map((solution, index) => (
                  <div key={solution.challengeId} className="rounded-md p-4" style={{border: '1px solid var(--surface-border)', backgroundColor: 'var(--editor-bg)'}}>
                    <div className="flex flex-wrap items-center justify-between gap-2 border-b pb-4" style={{borderColor: 'var(--surface-border)'}}>
                      <h3 className="text-lg font-semibold" style={{color: 'var(--text-color)'}}>Challenge {index + 1}</h3>
                      <div className="flex items-center gap-2">
                        <span className="rounded-full px-3 py-1 text-xs uppercase tracking-wider muted">{solution.language}</span>
                        <span className={`rounded-full px-3 py-1 text-xs font-semibold`} style={{backgroundColor: solution.score >= 80 ? 'rgba(59,130,246,0.06)' : 'rgba(240,68,68,0.06)'}}>{solution.score}%</span>
                      </div>
                    </div>

                    <div className="mt-4 space-y-4">
                      <div>
                        <div className="flex items-center gap-2 text-sm font-semibold" style={{color: 'var(--text-color)'}}>
                          <Code size={16} />
                          Code
                        </div>
                        <pre className="mt-2 max-h-[320px] overflow-auto rounded-md p-4" style={{backgroundColor: 'var(--exam-panel-bg)', color: 'var(--text-color)'}}>
                          <code>{solution.code}</code>
                        </pre>
                      </div>

                      {solution.results && (
                        <div className="space-y-3">
                          {solution.results.map((result, idx) => (
                            <div key={idx} className="rounded-md p-4" style={{border: '1px solid var(--surface-border)', backgroundColor: 'var(--exam-panel-bg)'}}>
                              <div className="flex items-center justify-between">
                                <span className="text-sm muted">Test case {idx + 1}</span>
                                <span className={`inline-flex items-center gap-1 rounded-full px-3 py-1 text-xs font-semibold`} style={{backgroundColor: result.passed ? 'rgba(16,185,129,0.06)' : 'rgba(240,68,68,0.06)'}}>{result.passed ? <Check size={12} /> : <X size={12} />}{result.passed ? 'Passed' : 'Failed'}</span>
                              </div>
                              <div className="mt-4 grid gap-3 text-sm lg:grid-cols-2">
                                <div>
                                  <p className="text-xs uppercase tracking-wider muted">Expected</p>
                                  <div className="mt-1 rounded-md p-3" style={{backgroundColor: 'var(--editor-bg)'}}><code>{result.expectedOutput}</code></div>
                                </div>
                                <div>
                                  <p className="text-xs uppercase tracking-wider muted">Actual</p>
                                  <div className="mt-1 rounded-md p-3" style={{backgroundColor: 'var(--editor-bg)'}}><code>{result.actualOutput}</code></div>
                                </div>
                              </div>
                              {result.error && <div className="mt-3 rounded-md p-3" style={{backgroundColor: 'rgba(240,68,68,0.04)'}}><p className="text-xs uppercase tracking-wider">Error</p><code>{result.error}</code></div>}
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                ))
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

const OverviewStat: React.FC<{ label: string; value: string | number }> = ({
  label,
  value,
}) => (
  <div className="flex flex-1 flex-col items-center justify-center gap-1 text-white">
    <span className="text-4xl font-semibold">{value}</span>
    <span className="text-xs uppercase tracking-[0.4em] text-gray-500">
      {label}
    </span>
  </div>
)

const scoreBadge = (score: number) => {
  if (score >= 90) return 'bg-emerald-500/10 text-emerald-200'
  if (score >= 80) return 'bg-primary-500/10 text-primary-200'
  if (score >= 70) return 'bg-amber-500/10 text-amber-200'
  if (score >= 60) return 'bg-rose-500/10 text-rose-200'
  return 'bg-rose-600/20 text-rose-100'
}

export default ExamSubmissionDetail
